                ; The following are MANDATORY and MUST NOT
                ; occur more than once
                ( ";" jspath-param ) /    ; see next section
                ( ";" "VALUE" "=" "TEXT")
                ;
                ; The following is OPTIONAL,
                ; and MAY occur more than once.
                ;
                (";" other-param)
                ;
                )

