dd.sourcecode:
created       = "CREATED" createdparam
                ":" timestamp CRLF

createdparam  = *(
                 ;
                 ; The following is OPTIONAL,
                 ; but MUST NOT occur more than once.
                 ;
                 (";" "VALUE" "=" "TIMESTAMP") /
                 ;
                 ; The following is OPTIONAL,
                 ; and MAY occur more than once.
                 ;
                 (";" any-param)
                 ;
                 )

pronouns       = "PRONOUNS" pronouns-param
                ":" text CRLF

pronouns-param =
                *(
                 ;
                 ; The following is OPTIONAL,
                 ; but MUST NOT occur more than once.
                 ;
                 (";" language-param) /
                 ;
                 ; The following is OPTIONAL,
                 ; and MAY occur more than once.
                 ;
                 (";" any-param)
                 ;
                 )
